function toggleMenu(){const e=document.querySelectorAll("[data-btnMenu]"),t=document.querySelector(".adm__sidebar");e.forEach(e=>{e.addEventListener("click",()=>{console.log("нажали кнопку меню"),"0"==t.style.width||"-450px"==t.style.left?(t.style.left="0",t.style.width="370px",t.style.display="flex"):(t.style.left="-450px",t.style.width="0",t.style.display="none")})})}toggleMenu();